// Extraer y limpiar la respuesta JSON de Gemini
const response = items[0].json;
let aiText = '';

// Extraer el texto de la respuesta de Gemini
if (response.candidates && response.candidates[0] && response.candidates[0].content && response.candidates[0].content.parts) {
  aiText = response.candidates[0].content.parts[0].text;
} else {
  return [{
    json: {
      error: 'No se pudo extraer respuesta de Gemini',
      originalResponse: response
    }
  }];
}

// Limpiar la respuesta (remover markdown si existe)
if (aiText.includes('```json')) {
  aiText = aiText.replace(/```json\n?/g, '').replace(/```/g, '').trim();
}

try {
  // Parsear el JSON
  const aiResult = JSON.parse(aiText);

  // Agregar datos del correo original
  const emailData = items[0].json.emailData || {};

  return [{
    json: {
      ...aiResult,
      correo_original: {
        subject: emailData.subject || '',
        from: emailData.from || '',
        date: emailData.date || '',
        text: emailData.text || ''
      }
    }
  }];
} catch (error) {
  return [{
    json: {
      error: 'Error parseando JSON de IA',
      aiText: aiText,
      parseError: error.message
    }
  }];
}

